import React, {
  Component
} from 'react';
import ReactNative, {
  Text,
  ListView,
  TouchableOpacity,
  View,
  SafeAreaView,
  Image,
  Platform,
  ScrollView,
  StatusBar,
} from 'react-native';
import PropTypes from 'prop-types';
import Carousel, { Pagination } from 'react-native-snap-carousel';
import { sliderWidth, itemWidth } from './styles/SliderEntry.style';
import SliderEntry2 from './components/SliderEntry2';
import styles, { colors } from './styles/index.style';
import { ENTRIES1, ENTRIES2 } from './static/entries';
import { scrollInterpolators, animatedStyles } from './utils/animations';

class Basket extends Component {


	_renderItem ({item, index}) {
	    return <SliderEntry2 data={item} even={(index + 1) % 2 === 0}/>;
	}

  _onPress(data) {
      this.props.onPress(data);
  }

    _renderItemWithParallax ({item, index}, parallaxProps) {
        return (
            <SliderEntry2
              data={item}
              even={(index + 1) % 2 === 0}
              idx={index}
              parallax={true}
              parallaxProps={parallaxProps}
              onPress={this.props.onPress}
              onPress2={this.props.onPress2}
              onPress3={this.props.onPress3}
            />
        );
    }

 


    render () {
    const { slider1ActiveSlide } = this.props.slider1ActiveSlide;

    if (this.props.isBasket === true) {
        return (
            <SafeAreaView style={styles.safeArea}>
                <View style={styles.container}>
                    <StatusBar
                      translucent={true}
                      backgroundColor={'rgba(0, 0, 0, 0.3)'}
                      barStyle={'light-content'}
                    />
                    <ScrollView
                      style={styles.scrollview}
                      scrollEventThrottle={200}
                      directionalLockEnabled={true}
                    >
                    <View style={styles.exampleContainer}>
                        <Carousel
                          ref={c => this._slider1Ref = c}
                          data={this.props.entries}
                          renderItem={this._renderItemWithParallax.bind(this)}
                          sliderWidth={sliderWidth}
                          itemWidth={itemWidth}
                          hasParallaxImages={true}
                          firstItem={0}
                          inactiveSlideScale={0.94}
                          inactiveSlideOpacity={0.7}
                          // inactiveSlideShift={20}
                          containerCustomStyle={styles.slider}
                          contentContainerCustomStyle={styles.sliderContentContainer}
                          loop={false}
                          loopClonesPerSide={2}
                          autoplay={false}
                          onSnapToItem={(index) => this.setState({ slider1ActiveSlide: index }) }
                        />
                    </View>
                    </ScrollView>
                </View>
            </SafeAreaView>
        );
    }
    return null;
	}
}


Basket.propTypes = {
  entries: PropTypes.array.isRequired,
  slider1ActiveSlide: PropTypes.number.isRequired,
  onPress: PropTypes.func.isRequired,
  onBasket: PropTypes.func,
}

Basket.defaultProps = {
  slider1ActiveSlide: 0,
  isBasket: false,
}

module.exports = Basket;
